import React from 'react';
import shopBag from './../images/shop-bag.svg';
import './../styles/Navbar.css';

const Navbar = (props) => {
    const count = props.count;
    let initialValue = 0;
    return (
        <div className="Nav-bar" onLoad={increaseCount}>
            <div className="Logo">
                <h2 className="Logo-text">Shopi</h2>
            </div>
            <div className="Cart-section">
                <img className="Cart-img" src={shopBag} alt="bagImage" />
                <div className="circle">0</div>
                <h3 className="Cart-text">Cart</h3>
            </div>
        </div>
    )

    function increaseCount() {
        const num = document.querySelector(".circle");
        let value = num.textContent;
        if(count == 1) {
            if((value == 0) || (value <= 4)) {
                initialValue++;
                num.textContent = initialValue;
                console.log(initialValue);
                let productName = selectedProduct.productname  +  '\t is added to the cart';
                showPopup(productName);
            }
            else if(value == 5) {
                num.textContent = value + '+';
                initialValue++;
                console.log(initialValue);
                let productName = selectedProduct.productname  +  '\t is added to the cart';
                showPopup(productName);
            }
            else {
                initialValue++;
                console.log(initialValue);
                let productName = selectedProduct.productname  +  '\t is added to the cart';
                showPopup(productName);
            } 
        }
    }

}

export default Navbar;
