import React, {useEffect, useState} from 'react';
import sunscreen from './../images/sunscreen.png';
import foundation from './../images/foundation.png';
import nailcolorRemove from './../images/nailcolor-remover.png';
import instaEyeliner from './../images/insta-eyeliner.png';
import facePack from './../images/sheet-facepack.png';
import shineEyeliner from './../images/shine-eyeliner.png';
import products from './../data';
import './../styles/Productscontainer.css';

const Productscontainer = () => {
    const [count, setCount] = useState(0);
    // useEffect( () => {
    //     setCount(count + 1);
    // })
    var productsData = JSON.parse(data);
    console.log(productsData);
    return (
        <div className="Product-container" onLoad={showProducts}>
            <div class="Product-card">
                <div class="Product-image">
                    <img src={sunscreen} alt="img" />
                </div>
                <div class="Product-details">
                    <h3 class="Product-name">Face Powder With Sunscreen</h3>
                    <h4 class="Product-price">$500</h4>
                    <h5 class="Add-button">Add to Cart</h5>
                </div>
            </div>
            <div class="Product-card">
                <div class="Product-image">
                    <img src={foundation} alt="img" />
                </div>
                <div class="Product-details">
                    <h3 class="Product-name">Weightless Mousse Foundation</h3>
                    <h4 class="Product-price">$350</h4>
                    <h5 class="Add-button">Add to Cart</h5>
                </div>
            </div>
            <div class="Product-card">
                <div class="Product-image">
                    <img src={nailcolorRemove} alt="img" />
                </div>
                <div class="Product-details">
                    <h3 class="Product-name">Nail Color Remove</h3>
                    <h4 class="Product-price">$120</h4>
                    <h5 class="Add-button">Add to Cart</h5>
                </div>
            </div>
            <div class="Product-card">
                <div class="Product-image">
                    <img src={instaEyeliner} alt="img" />
                </div>
                <div class="Product-details">
                    <h3 class="Product-name">Insta Eye Liner</h3>
                    <h4 class="Product-price">$300</h4>
                    <h5 class="Add-button">Add to Cart</h5>
                </div>
            </div>
            <div class="Product-card">
                <div class="Product-image">
                    <img src={facePack} alt="img" />
                </div>
                <div class="Product-details">
                    <h3 class="Product-name">Blush & Glow Strawberry Sheet Mask</h3>
                    <h4 class="Product-price">$180</h4>
                    <h5 class="Add-button">Add to Cart</h5>
                </div>
            </div>
            <div class="Product-card">
                <div class="Product-image">
                    <img src={shineEyeliner} alt="img" />
                </div>
                <div class="Product-details">
                    <h3 class="Product-name">Absolute Shine Line Eye Liner</h3>
                    <h4 class="Product-price">$750</h4>
                    <h5 class="Add-button">Add to Cart</h5>
                </div>
            </div> 
        </div>
    )

    function showProducts() {
        const addToCartBtn = document.querySelectorAll(".Add-button");
        addToCartBtn.forEach(addBtn => {
            addBtn.addEventListener('click', () => { setCount(count + 1)
                addBtn.innerHTML = "Visit Cart";
                addBtn.removeAttribute("className", "Add-button")
                addBtn.setAttribute("id", "View-button");
            })
        });  
    }
}

export default Productscontainer;




